void draw(Points& pts, GLenum mode)
{
	glBegin(mode);
	for (Points::iterator it = pts.begin(); it != pts.end(); ++it)
	{
		// Remap coordinate
		float x = it->x * 2 - 1.0;
		float y = it->y * 2 - 1.0;
		glVertex2f(x, y);
	}
	glEnd();
}

void drawOutline(Points& pts, float size)
{
	glLineWidth(size);
	draw(pts, GL_LINE_LOOP);
}

void drawLine(Points& pts, float size)
{
	glLineWidth(size);
	draw(pts, GL_LINE_STRIP);
}

void drawPoly(Points& pts)
{
	draw(pts, GL_POLYGON);
}

void drawPoly(Points& pts, float size)
{		
	/*Draws poly with black outline*/
	drawPoly(pts);
	glPushAttrib(GL_CURRENT_BIT);
	glColor3f(0.0, 0.0, 0.0);
	drawOutline(pts, size);
	glPopAttrib();
}

void drawTriFan(Points& pts)
{
	draw(pts, GL_TRIANGLE_FAN);
}

void drawTooth(Points& pts)
{
	drawPoly(pts, 1);
}


void display(void)
{
	glClear(GL_COLOR_BUFFER_BIT | GL_DEPTH_BUFFER_BIT);

	// Calculate the skin tone ratio
	const float dark_ratio = 0.7;
	float tone = dark_ratio + (1.0-dark_ratio)*cos(0.5*time);

	// NOTE: The body is fixed, not controllable
	glColor3f(tone*0.976, tone*0.903, tone*0.702);
	drawPoly(body, 2.0);

	glColor3f(0.1, 0.1, 0.1);
	drawLine(chestline1, 2.0);
	drawLine(chestline2, 2.0);
	drawLine(neckline1, 2.0);
	drawLine(neckline2, 2.0);

	glColor4f(0.0, 0.0, 0.0, 0.2);
	drawPoly(chestshade1);
	drawPoly(chestshade2);
	drawPoly(chestshade3);

	glPushMatrix();

	//controls transformation
	glScalef(k, k, k);	
	glTranslatef(tx, ty, 0);	
	glRotatef(alpha, 0, 0, 1);
	
	//Back hair
	glColor3f(0.0, 0.0, 0.0);
	drawPoly(dhair5);
	drawPoly(dhair8);

	glColor3f(0.120, 0.100, 0.035);
	drawPoly(hair9);
	drawPoly(hair10);
	drawPoly(hair11);
	drawPoly(hair12);
	drawPoly(hair13);
	drawPoly(hair14);

	//Face
	glColor3f(tone*0.976, tone*0.903, tone*0.702);
	drawPoly(face, 2.0);
	drawPoly(l_ear);
	drawPoly(r_ear);

	glColor4f(0.7, 0.2, 0.0, 1-cos(0.5*time)); //alpha changes with time
	for (vector<Points>::iterator it = veinlines.begin(); it != veinlines.end(); ++it)
		drawLine(*it, 2);

	//Eyes
	glColor4f(0.0, 0.0, 0.0, 0.2);
	drawPoly(r_eyesocket);
	drawPoly(l_eyesocket);
	
	drawPoly(r_innerear);
	drawPoly(l_innerear);

	glColor3f(0.01, 0.01, 0.01);
	drawPoly(r_eyelid);
	glColor3f(1.0, 1.0, 1.0);
	drawPoly(r_eye);
	glColor3f(0.0, 0.3, 0.3);
	drawPoly(r_eyeball);
	// Rage mode
	glColor4f(0.239, 0.910, 0.180, 1 - cos(0.5*time));
	drawPoly(r_eye);
	
	// Offset the left eye a little
	glPushMatrix();
	glRotatef(-2, 0, 0, 1);
	glTranslatef(-0.01, 0.01, 0);
	glColor3f(0.01, 0.01, 0.01);
	drawPoly(l_eyelid);
	glColor3f(1.0, 1.0, 1.0);
	drawPoly(l_eye);
	glColor3f(0.0, 0.3, 0.3);
	drawPoly(l_eyeball);
	// Rage mode
	glColor4f(0.239, 0.910, 0.180, 1 - cos(0.5*time));
	drawPoly(l_eye);
	glPopMatrix();
	
    // Teeth
	glColor3f(1.0, 1.0, 1.0);
	for (vector<Points>::iterator it = teeth.begin(); it != teeth.end(); ++it)
	{
		drawTooth(*it);
	}

	// Use the right side, but flipped
	glPushMatrix();
	glTranslatef(0.01, 0, 0);
	glScalef(-1, 1.002, 1);
	for (vector<Points>::iterator it = teeth.begin(); it != teeth.end(); ++it)
	{
		drawTooth(*it);
	}
	glPopMatrix();

	glColor4f(0.0, 0.0, 0.0, 0.2);
	drawTriFan(nose_shade2); // Better than using Polygon

	glColor3f(0.0, 0.0, 0.0);
	drawLine(nose_line, 1);

	// Dark Hair
	glColor3f(0.0, 0.0, 0.0);
	drawPoly(dhair);
	drawPoly(dhair1);
	drawPoly(dhair2);
	drawPoly(dhair3);
	drawPoly(dhair4);
	drawPoly(dhair6);
	drawPoly(dhair7);
	drawPoly(dhair9);

	// Light Hair
	glColor3f(0.120, 0.100, 0.035);
	drawPoly(hair3);
	drawPoly(hair4);
	drawPoly(hair5);
	drawPoly(hair6);
	drawPoly(hair7);
	drawPoly(hair8);

	drawPoly(hair15);
	drawPoly(hair16);
	drawPoly(hair17);
	drawPoly(hair18);

	glPopMatrix();
	glFlush ();
}

void reshape (int w, int h)
{
	glViewport (0, 0, (GLsizei) w, (GLsizei) h);

	glMatrixMode (GL_PROJECTION);
	glLoadIdentity();

	// Screen view space
	glOrtho(-1, 1, -1, 1,-1, 1);
	glMatrixMode(GL_MODELVIEW);
	glLoadIdentity();
}

void init(void)
{
	glBlendFunc(GL_SRC_ALPHA, GL_ONE_MINUS_SRC_ALPHA);
	glEnable(GL_BLEND);
	glClearColor (0.1, 0.0, 0.0, 0.0); // Initial color
	glShadeModel (GL_SMOOTH);
}

void idle(void)
{
	time = glutGet(GLUT_ELAPSED_TIME)/1000.0;
	glutPostRedisplay(); //Force redraw everyframe
}

void changeAlpha(float amount)
{
	float ang = alpha + amount;
	if (abs(ang) < TILT_MAX)
	{
		alpha = ang;
		glutPostRedisplay();
	}
}

void changeTx(float amount)
{
	float x = tx + amount;
	if (abs(x) < TX_MAX)
	{
		tx = x;
		glutPostRedisplay();
	}
}

void changeTy(float amount)
{
	float y = ty + amount;
	if (abs(y) < TY_MAX)
	{
		ty = y; 
		glutPostRedisplay();
	}
}

void changeScale(float amount)
{
	float scale = k + amount;
	if (scale < SCALE_MAX && scale > SCALE_MIN)
	{
		k = scale;
		glutPostRedisplay();
	}
}

void keyboard (unsigned char key, int x, int y)
{
	//keys to control scaling - k
	//keys to control rotation - alpha
	//keys to control translation - tx, ty
	switch (key) {

		case 'a':
			changeAlpha(5);
		break;

		case 'd':
			changeAlpha(-5);
		break;

		case 'q':
			changeScale(0.05);
		break;

		case 'e':
			changeScale(-0.05);
		break;

		case 'z':
			changeTx(-0.05);
		break;

		case 'c':
			changeTx(0.05);
		break;

		case 's':
			changeTy(-0.05);
		break;

		case 'w':
			changeTy(+0.05);
		break;
			
		case 27:
			exit(0);
		break;

		default:
		break;
	}
}

int main(int argc, char **argv)
{
	cout<<"CS3241 Lab 1\n\n";
	cout<<"+++++HEAD CONTROLS+++++++\n\n";
	cout<<"Scale: Q/E\n";
	cout<<"Tilt: A/D\n";
	cout<<"Move Up/Down: W/S\n";
	cout<<"Move Left/Right: Z/C\n";
	cout <<"ESC: Quit\n";

	glutInit(&argc, argv);
	glutInitDisplayMode (GLUT_SINGLE | GLUT_RGB | GLUT_DEPTH);
	glutInitWindowSize (SCREEN_W, SCREEN_H);
	glutInitWindowPosition (50, 50);
	glutCreateWindow (argv[0]);
	init ();
	glutDisplayFunc(display);
	glutReshapeFunc(reshape);
	//glutMouseFunc(mouse);
	glutIdleFunc(idle);
	glutKeyboardFunc(keyboard);
	glutMainLoop();

	return 0;
}
